import React, { useState, useEffect, useReducer } from 'react';

import Card from '../UI/Card/Card';
import classes from './Login.module.css';
import Button from '../UI/Button/Button';

//reducer function outside of component function because it doesn't require
//any data inside component
const emailReducer = (state, action) => {
  if (action.type === 'USER_INPUT') {
    return {value: action.value, isValid: action.value.includes('@')};
  }

  if (action.type === "USER_BLUR") {
    return {value: state.value, isValid: state.value.includes('@')}
  }
  return {value: '',
isValid: false}
}

const Login = (props) => {
  const [enteredPassword, setEnteredPassword] = useState('');
  const [passwordIsValid, setPasswordIsValid] = useState();
  const [formIsValid, setFormIsValid] = useState(false);


  //const [state, dispatchFunction] = useReducer(reducerFunction, initialState, functionToSetInitialState)
const [emailState, dispatchEmail] = useReducer(emailReducer, {value: '',
isValid: false})


/*
useEffect(() => {
const formValidChecker = setTimeout(() => {
  console.log("checking form validity");
  setFormIsValid(
    emailState.isValid && enteredPassword.trim().length > 6
  )
}, 500)

return () => {
  console.log("CLEANUP")
  clearTimeout(formValidChecker);
}  
}, [emailState.isValid, enteredPassword])
*/




  const emailChangeHandler = (event) => {
  dispatchEmail({type: 'USER_INPUT',
  value: event.target.value
});

  };

  const passwordChangeHandler = (event) => {
    setEnteredPassword(event.target.value);


  };

  const validateEmailHandler = () => {
  dispatchEmail({
    type: "USER_BLUR"
  })
  };

  const validatePasswordHandler = () => {
    setPasswordIsValid(enteredPassword.trim().length > 6);
  };

  const submitHandler = (event) => {
    event.preventDefault();
    props.onLogin(emailState.value, enteredPassword);
  };

  return (
    <Card className={classes.login}>
      <form onSubmit={submitHandler}>
        <div
          className={`${classes.control} ${
            emailState.isValid === false ? classes.invalid : ''
          }`}
        >
          <label htmlFor="email">E-Mail</label>
          <input
            type="email"
            id="email"
            value={emailState.value}
            onChange={emailChangeHandler}
            onBlur={validateEmailHandler}
          />
        </div>
        <div
          className={`${classes.control} ${
            passwordIsValid === false ? classes.invalid : ''
          }`}
        >
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={enteredPassword}
            onChange={passwordChangeHandler}
            onBlur={validatePasswordHandler}
          />
        </div>
        <div className={classes.actions}>
          <Button type="submit" className={classes.btn} disabled={!formIsValid}>
            Login
          </Button>
        </div>
      </form>
    </Card>
  );
};

export default Login;
